package com.taulukko.commons.util.web;

import java.io.File;

import org.junit.Assert;
import org.junit.Test;

import com.taulukko.commons.TaulukkoException;

public class HTMLUtilsTest {

	public static final String IMAGE_B64_TEST = "data:image/png;base64,";

	private static ClassLoader CLASSLOADER = HTMLUtilsTest.class.getClassLoader();

	@Test
	public void encodeFileToBase64Binary() throws TaulukkoException {

		File file = new File(CLASSLOADER.getResource("logo.png").getFile());
		String out = HTMLUtils.encodeFileToBase64Binary(file);

		Assert.assertEquals(IMAGE_B64_TEST,"data:image/png;base64,"+ out);
	}
 
}